package primeiro.projeto;

public class PreePosIncremento {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		//int b = a++;

		System.out.println("int a = 10; a++ fica = " + a++);
		a = 10;
		System.out.println("int a = 10; ++a fica = " + ++a);
		a = 10;
		System.out.println("int a = 10; a-- fica = " + a--);
		a = 10;
		System.out.println("int a = 10; --a fica = " + --a);
		

		
	
	}

}
